#设置服务器端编码
SET NAMES UTF8;

#删除数据库yw,如果存在
DROP DATABASE IF EXISTS ywvue;

#创建数据库ywvue
CREATE DATABASE ywvue CHARSET=UTF8;

#使用数据库ywvue
USE ywvue;

#创建数据表artist-作家
CREATE TABLE artist(
    aid INT PRIMARY KEY AUTO_INCREMENT,
    aname VARCHAR(10),
    info  VARCHAR(100),
    img VARCHAR(128)
);

#创建数据表product-作品
CREATE TABLE product(
    pid INT PRIMARY KEY AUTO_INCREMENT,      
    pname VARCHAR(20),				#作品名
    price DECIMAL(20,2),			#价钱
    atrist VARCHAR(20),				#作家
    material VARCHAR(20),			#材质
    size VARCHAR(20),				#尺寸
    timer VARCHAR(20),				#创作时间
    class VARCHAR(20),				#作品类型
    style VARCHAR(20),				#风格
    topic VARCHAR(20),				#题材
    shape VARCHAR(20),				#形状
    space VARCHAR(20),				#摆放空间
    lable VARCHAR(20),				#标签
    decoration VARCHAR(20),			#装裱费
    transport VARCHAR(20),			#运费
    is_recommend BOOLEAN,			#是否推荐
    rec_reason VARCHAR(1000),			#编辑推荐
    img VARCHAR(128),				#图片
    imgs VARCHAR(512),				#图集
	spaceshow VARCHAR(1000),		#场景展示
	otherdetail VARCHAR(128),		#其他细节展示
	buynotes VARCHAR(128),			#购买须知
	ywtips VARCHAR(128),			#艺网小贴士
    detail VARCHAR(10000)			#详情
);

#创建数据表article-文章
CREATE TABLE article(
    nid INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(268),				#文章标题
    tread VARCHAR(20),				#阅读量
    columns VARCHAR(20),			#所属栏目
    is_special BOOLEAN,				#是否推荐
	istop BOOLEAN,					#是否头条
	label VARCHAR(200),				#文章标签
    img VARCHAR(128),				#图片
    description VARCHAR(268),		#文章简介
    detail VARCHAR(3000)			#文章详情
);

#创建数据表user-用户信息
CREATE TABLE user(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(20),				#用户名
    upwd VARCHAR(20)				#密码
);

#创建数据表shopcart-购物车
CREATE TABLE shopcart(
    cid INT PRIMARY KEY AUTO_INCREMENT,
    pid INT,					#商品ID
    uid INT,					#用户ID
    count INT,					#订单数量
    price DECIMAL(20,2),		#单价
    ctime DATETIME,				#加入时间
	isdel BOOLEAN DEFAULT 0		#是否删除
);

#创建数据表comment-评论
CREATE TABLE comment(
    mid INT PRIMARY KEY AUTO_INCREMENT,
    pid INT,				#作品ID
    uid INT,				#用户ID
    content VARCHAR(200),		#评论内容
    mtime DATETIME			#评论时间
);

